mapper.insert:

  SELECT LAST_INSERT_ID()

insert into path (startplace, endplace, priceperunit
  )
values (#{startplace,jdbcType=VARCHAR}, #{endplace,jdbcType=VARCHAR}, #{priceperunit,jdbcType=DOUBLE}
  )

mapper.update:
update path
set startplace = #{startplace,jdbcType=VARCHAR},
  endplace = #{endplace,jdbcType=VARCHAR},
  priceperunit = #{priceperunit,jdbcType=DOUBLE}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, startplace, endplace, priceperunit
from path
where id = #{id,jdbcType=INTEGER}
