mapper.update:
update authority
set name = #{name,jdbcType=VARCHAR},
  descr = #{descr,jdbcType=VARCHAR},
  by1 = #{by1,jdbcType=VARCHAR},
  by2 = #{by2,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, name, descr, by1, by2
from authority
where id = #{id,jdbcType=BIGINT}

