mapper.update:
update car
set numberring = #{numberring,jdbcType=VARCHAR},
  license_plate = #{licensePlate,jdbcType=VARCHAR},
  route_id = #{routeId,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, numberring, license_plate, route_id
from car
where id = #{id,jdbcType=INTEGER}

